import React, { Component } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
class Dashboard extends Component {
  constructor() {
    super();
    this.state = {
      EMail: "",
      EventName: "",
      EventType: "",
      EventStart: "",
      EventEnd: "",
      NoPeople: "",
      EventPlace: "",
      errors: {}
    };
  }
  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };
  onSubmit = e => {
    e.preventDefault();
const newEvent = {
      EMail: this.state.EMail,
      EventName: this.state.EventName,
      EventType: this.state.EventType,
      EventStart: this.state.EventStart,
      EventEnd: this.state.EventEnd,
      NoPeople: this.state.NoPeople,
      EventPlace: this.state.EventPlace,
      errors: {}

    };
console.log(newEvent);
  };
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };
render() {
  const { errors } = this.state;

    const { user } = this.props.auth;
return (
      <div style={{ height: "75vh" }} className="container valign-wrapper">
        <div className="row">
          <div className="col s12 center-align">
            <h4>
              <b>Hey there,</b> {user.name.split(" ")[0]}
              <p className="flow-text grey-text text-darken-1">
                You are logged into a full-stack{" "}
                <span style={{ fontFamily: "monospace" }}>MERN</span> app üëè
              </p>
            </h4>
            <div className="container">
        <div className="row">
          <div className="col s8 offset-s2">
            <Link to="/" className="btn-flat waves-effect">
              <i className="material-icons left">keyboard_backspace</i> Back to
              home
            </Link>
            <div className="col s12" style={{ paddingLeft: "11.250px" }}>
              <h4>Enter Event Details</h4>
            </div>
            <form noValidate onSubmit={this.onSubmit}>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.EMail}
                  error={errors.EMail}
                  id="EMail"
                  type="email"
                />
                <label htmlFor="EMail">Email</label>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.EventName}
                  error={errors.EventName}
                  id="EventName"
                  type="text"
                />
                <label htmlFor="EventName">Name of Event</label>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.EventType}
                  error={errors.EventType}
                  id="EventType"
                  type="text"
                />
                <label htmlFor="EventType">Type of Event</label>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.EventStart}
                  error={errors.EventStart}
                  id="EventStart"
                  type="date"
                />
                <label htmlFor="EventStart">Starting Date of Event </label>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.EventEnd}
                  error={errors.EventEnd}
                  id="EventEnd"
                  type="date"
                />
                <label htmlFor="EventEnd">Ending Date of Event</label>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.NoPeople}
                  error={errors.NoPeople}
                  id="NoPeople"
                  type="number"
                />
                <label htmlFor="NoPeople">Expected Number of People In Event</label>
              </div>
              <div className="input-field col s12">
                <input
                  onChange={this.onChange}
                  value={this.state.EventPlace}
                  error={errors.EventPlace}
                  id="EventPlace"
                  type="text"
                />
                <label htmlFor="EventPlace">Place of Event</label>
              </div>
              <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                <button
                  style={{
                    width: "150px",
                    borderRadius: "3px",
                    letterSpacing: "1.5px",
                    marginTop: "1rem"
                  }}
                  type="submit"
                  className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                >
                  Register Event
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
            <button
              style={{
                width: "150px",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem"
              }}
              onClick={this.onLogoutClick}
              className="btn btn-large waves-effect waves-light hoverable blue accent-3"
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    );
  }
}
Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth
});
export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard);
